    const normalizeTrackedSymbol = (symbol: string) => {
        const upper = String(symbol || "").toUpperCase();
        if (upper === "TBNB" || upper === "WBNB") return "BNB";
        if (upper === "ASTR") return "ASTER";
        if (upper === "TMATIC" || upper.endsWith("ATIC")) return "POL";
        return upper;
    };

    const autoTradeCandidateSymbols = useMemo(() => {
        const chainIds = activeChains.map((chain) => chain === "BNB" ? 56 : 137);
        const symbols = new Set<string>();

        chainIds.forEach((id) => {
            Object.keys(TOKEN_REGISTRY[id] || {}).forEach((symbol) => {
                const normalized = normalizeTrackedSymbol(symbol);
                if (isInterestingToken(normalized)) {
                    symbols.add(normalized);
                }
            });
        });

        ["BNB", "ETH", "LINK", "WLFI", "ASTER", "CAKE", "SHIB", "POL"].forEach((symbol) => {
            if (isInterestingToken(symbol)) {
                symbols.add(symbol);
            }
        });

        return Array.from(symbols);
    }, [activeChains]);

