    const getUsdPriceForSymbol = useCallback((symbol: string) => {
        const normalized = normalizeTrackedSymbol(symbol);
        if (TRADE_CONFIG.STABLECOINS.includes(normalized)) return 1;
        return allMarketPrices[normalized]?.price || initialData[normalized]?.price || 0;
    }, [allMarketPrices]);

    const getSpendableUsdForAsset = useCallback((asset?: LiveWalletAsset | null) => {
        if (!asset) return 0;
        const reserveUsd = asset.symbol === "BNB" || asset.symbol === "POL"
            ? MIN_NATIVE_GAS_RESERVE_USD
            : 0;
        return Math.max(0, asset.usdValue - reserveUsd);
    }, []);

    const getLiveFundingCandidates = useCallback((requestedSymbol?: string) => {
        const normalizedRequested = requestedSymbol ? normalizeTrackedSymbol(requestedSymbol) : "";
        const assets = Object.values(liveWalletAssetsRef.current)
            .map((asset) => ({
                ...asset,
                spendableUsd: getSpendableUsdForAsset(asset),
            }))
            .filter((asset) => asset.amount > 0 && asset.spendableUsd > 0.05)
            .sort((left, right) => right.spendableUsd - left.spendableUsd);

        const stableCandidates = assets.filter((asset) =>
            asset.isStable && (!normalizedRequested || isTokenSupportedOnChain(normalizedRequested, asset.chainId))
        );
        const nonStableCandidates = assets.filter(
            (asset) =>
                !asset.isStable &&
                asset.symbol !== normalizedRequested &&
                (!normalizedRequested || isTokenSupportedOnChain(normalizedRequested, asset.chainId))
        );

        return {
            assets,
            stableCandidates,
            nonStableCandidates,
            fundedStableCandidates: stableCandidates.filter((asset) => asset.spendableUsd >= MIN_LIVE_ORDER_USD),
            fundedNonStableCandidates: nonStableCandidates.filter((asset) => asset.spendableUsd >= MIN_LIVE_ORDER_USD),
        };
    }, [getSpendableUsdForAsset]);

