        const dedupeKey = `${agentId}:${actualType}:${content}`;
        const now = Date.now();
        const lastMessageAt = recentMessageTimestampsRef.current.get(dedupeKey) ?? 0;

        if (now - lastMessageAt < 30_000) {
            return {
                id: dedupeKey,
                agentId,
                content,
                timestamp: lastMessageAt,
                type: actualType,
            } satisfies Message;
        }

        recentMessageTimestampsRef.current.set(dedupeKey, now);
        const newMessage: Message = {
            id: Math.random().toString(36).substring(7),
            agentId,
            content,
            timestamp: now,
            type: actualType,
        };
        setMessages((prev) => [...prev, newMessage]);

